mapper.select:
SELECT
	t.*
FROM
	(
		SELECT
			f.RESOURCE_ID
		FROM
			(
				SELECT
					a.ROLE_ID
				FROM
					sys_user_role a
				LEFT JOIN sys_role b ON a.ROLE_ID = b.ID
				WHERE
					a.USER_ID = #{userId}
				AND b.STATUS = #{status}
				AND b.ORG_CODE = #{orgCode}
			) g
		LEFT JOIN sys_role_resource f ON f.ROLE_ID = g.ROLE_ID AND g.ORG_CODE = #{orgCode}
		GROUP BY
			f.RESOURCE_ID
	) w
LEFT JOIN sys_resource t ON w.RESOURCE_ID = t.ID AND t.ORG_CODE = #{orgCode}
WHERE
	t. STATUS = #{status}
